mapper.update:
UPDATE REPLY
SET COMMENT_CONTENTS=#{comment_contents}
where
comment_num=#{comment_num}

mapper.select:

		select 
		*
		from ( 
		select 
		/*+ INDEX_DESC(community pk_board) */ 
		rownum rn, 
		community_num,community_category,community_title,
		community_date, community_map, user_id,
		community_place, community_schedule
	 	from
   		 	community
   		 	 where 



   			rownum<= #{pageNum} * #{amount}
   			)

   			where rn>(#{pageNum}-1) * #{amount}
